import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthState } from './types';
import { authService } from './services/auth.ts';

// Pages
import Home from './pages/Home.tsx';
import DashboardPage from './pages/DashboardPage.tsx';
import EditorPage from './pages/EditorPage.tsx';
import PreviewPage from './pages/PreviewPage.tsx';

// Auth Components
import Login from './components/auth/Login.tsx';
import SignUp from './components/auth/SignUp.tsx';
import Profile from './components/auth/Profile.tsx';

const App: React.FC = () => {
  const [authState, setAuthState] = useState<AuthState>({
    user: null,
    isAuthenticated: false,
    isLoading: true
  });

  useEffect(() => {
    // Subscribe to auth state changes
    const unsubscribe = authService.subscribe((state) => {
      setAuthState(state);
    });

    // Cleanup subscription on unmount
    return unsubscribe;
  }, []);

  const handleLogin = async (credentials: { email: string; password: string }) => {
    try {
      await authService.login(credentials);
    } catch (error) {
      console.error('Login failed:', error);
      alert('Login failed. Please check your credentials.');
    }
  };

  const handleSignUp = async (data: { name: string; email: string; password: string }) => {
    try {
      await authService.signup(data);
    } catch (error) {
      console.error('Sign up failed:', error);
      alert('Sign up failed. Please try again.');
    }
  };

  const handleLogout = async () => {
    try {
      await authService.logout();
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const handleUpdateProfile = async (data: any) => {
    try {
      await authService.updateProfile(data);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Profile update failed:', error);
      alert('Failed to update profile. Please try again.');
    }
  };

  // Protected Route Component
  const ProtectedRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    if (authState.isLoading) {
      return (
        <div className="min-h-screen flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
        </div>
      );
    }

    if (!authState.isAuthenticated) {
      return <Navigate to="/login" replace />;
    }

    return <>{children}</>;
  };

  // Public Route Component (redirect if authenticated)
  const PublicRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    if (authState.isLoading) {
      return (
        <div className="min-h-screen flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
        </div>
      );
    }

    if (authState.isAuthenticated) {
      return <Navigate to="/dashboard" replace />;
    }

    return <>{children}</>;
  };

  if (authState.isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading Portfolio Builder...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          
          <Route 
            path="/login" 
            element={
              <PublicRoute>
                <Login onLogin={handleLogin} isLoading={authState.isLoading} />
              </PublicRoute>
            } 
          />
          
          <Route 
            path="/signup" 
            element={
              <PublicRoute>
                <SignUp onSignUp={handleSignUp} isLoading={authState.isLoading} />
              </PublicRoute>
            } 
          />

          {/* Protected Routes */}
          <Route 
            path="/dashboard" 
            element={
              <ProtectedRoute>
                <DashboardPage 
                  user={authState.user || undefined} 
                  onLogout={handleLogout} 
                />
              </ProtectedRoute>
            } 
          />
          
          <Route 
            path="/editor/:portfolioId" 
            element={
              <ProtectedRoute>
                <EditorPage 
                  user={authState.user || undefined} 
                  onLogout={handleLogout} 
                />
              </ProtectedRoute>
            } 
          />
          
          <Route 
            path="/profile" 
            element={
              <ProtectedRoute>
                <Profile 
                  user={authState.user!} 
                  onUpdateProfile={handleUpdateProfile}
                  onLogout={handleLogout} 
                />
              </ProtectedRoute>
            } 
          />

          {/* Public Portfolio Preview Routes */}
          <Route 
            path="/preview/:portfolioId" 
            element={<PreviewPage />} 
          />
          
          <Route 
            path="/portfolio/:slug" 
            element={<PreviewPage />} 
          />

          {/* 404 Route */}
          <Route 
            path="*" 
            element={
              <div className="min-h-screen flex items-center justify-center bg-gray-50">
                <div className="text-center">
                  <h1 className="text-4xl font-bold text-gray-900 mb-4">404</h1>
                  <p className="text-gray-600 mb-4">Page not found</p>
                  <a href="/" className="text-indigo-600 hover:text-indigo-500">
                    Return to Home
                  </a>
                </div>
              </div>
            } 
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
